import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import Typography from "@material-ui/core/Typography";
import Link from "@material-ui/core/Link";
import { useDispatch, useSelector } from 'react-redux'

import { fetchUserInfo } from "../actions/auth";

const useStyles = makeStyles(theme => ({
  toolbar: {
    borderBottom: `1px solid ${theme.palette.divider}`
  },
  toolbarTitle: {
    flex: 1
  },
  toolbarSecondary: {
    justifyContent: "space-between",
    overflowX: "auto"
  },
  toolbarLink: {
    padding: theme.spacing(1),
    flexShrink: 0
  }
}));

const myfunc = () => {
  console.log('here');
}

export default function Header(props) {
  const [fetchFired, setFetchFired] = useState(false);
  const classes = useStyles();
  const { sections, title } = props;
  let firstNameDisplay;
  let firstNameLocalStorage;

  const { isValidToken, firstName } = useSelector(state => state.auth);
  console.log('isValidToken', isValidToken)
  console.log('firstName', firstName)

  if (typeof window !== 'undefined') {
    // Server side rendering protection
    firstNameLocalStorage = localStorage.getItem("firstName");
  }

  // First time effect
  useEffect(() => {
  }, []);


  // First time effect and when isValidToken changes
  useEffect(() => {
   if (isValidToken) {
     // So the token is present and valid, do I have the user details?
     if (!firstNameLocalStorage) {
       // Nope, I should try and get them
       const dispatch = useDispatch();
       dispatch(fetchUserInfo());
     }
   }
  }, [isValidToken]);

  useEffect(() => {
   console.log('firstName')
  }, [firstName]);


  return (
    <>
      <Toolbar className={classes.toolbar}>
        <Button size="small">Subscribe</Button>
        <Typography
          component="h2"
          variant="h5"
          color="inherit"
          align="center"
          noWrap
          className={classes.toolbarTitle}
        >
          {title}
        </Typography>
        <IconButton>
          <SearchIcon />
        </IconButton>
        <Button
          variant="outlined"
          size="small"
          onClick={() => {
            window.location = "/connect/";
          }}
        >
          {firstNameDisplay || `Sign up / Sign in`}
        </Button>
      </Toolbar>
      <Toolbar
        component="nav"
        variant="dense"
        className={classes.toolbarSecondary}
      >
        {sections.map(section => (
          <Link
            color="inherit"
            noWrap
            key={section.title}
            variant="body2"
            href={section.url}
            className={classes.toolbarLink}
          >
            {section.title}
          </Link>
        ))}
      </Toolbar>
    </>
  );
}

Header.propTypes = {
  sections: PropTypes.array,
  title: PropTypes.string
};
