  }

  return (
    <div>
      <Head>
        <title>{debate.slug}</title>
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
      </Head>
      <>
        <CssBaseline />
        <Container maxWidth="lg">
          <Header title={debate.slug} header={debate.header} />
