import { takeLatest, put } from "redux-saga/effects";
import { FETCH_POSTS_REQUEST, fetchPostsSuccess, fetchPostsFailure } from "../actions/posts";
import { get } from "../lib/api";

function* fetchPosts(action) {
  var r = yield get("v1/fetchPosts?id=".concat(action.accountId)).then(function (json) {
    return put(fetchPostsSuccess(json));
  })["catch"](function (err) {
    return put(fetchPostsFailure(err));
  });
  yield r;
}

export default function* postsSaga() {
  yield takeLatest(FETCH_POSTS_REQUEST, fetchPosts);
}
