import React, { useEffect } from "react";
import Head from 'next/head'

import nextI18NextInstance from '../../i18n';

import config from '../../config';

const getCurrentLang = () => nextI18NextInstance.i18n.language || 'en';

export default function CMSPage(page) {
  console.log('page', page);

  // Fetch the page
  useEffect(() => {
    // copy to local values
    console.log('page', page)
    if (page) {
    }
  }, [page]);

  return (
    <div>
      <Head>
        <title>CMSPAGE</title>
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
      </Head>
      <p>hello</p>
    </div>
  );
}

CMSPage.getInitialProps = async ctx => {
  // Fetch the page contents
  const res = await fetch(`${config.api.host}/v1/fetchPage?id=${ctx.query.page}`)
  const page = await res.json()

  console.log('page', page)
  return page
}
