import { takeLatest, put } from "redux-saga/effects";
import { FETCH_MODULE_REQUEST, fetchModuleSuccess, fetchModuleFailure } from "../actions/modules";
import { get } from "../lib/api";

function* fetchModule(action) {
  var r = yield get("v1/fetchModule?id=".concat(action.id)).then(function (json) {
    return put(fetchModuleSuccess(json));
  })["catch"](function (err) {
    return put(fetchModuleFailure(err));
  });
  yield r;
}

export default function* modulesSaga() {
  yield takeLatest(FETCH_MODULE_REQUEST, fetchModule);
}
