import React, { useEffect } from "react";
import Head from 'next/head'
import { withRouter } from 'next/router'

function CMSPage(props) {
  const { router: { query: { page } } } = props;

  console.log('page', page);

  // Fetch the page
  useEffect(() => {
    // copy to local values
    console.log('page', page)
    if (page) {
        setName(page.name);
        setType(page.type);
        setUrl(page.url);
        setEditorValueEnglish(page.languages.english);
        setEditorValueFrench(page.languages.french);
        setEditorValueSpanish(page.languages.spanish);
        setVersion('english')
    }
  }, [page]);

  return (
    <div>
      <Head>
        <title>CMSPAGE</title>
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
      </Head>
      <p>hello</p>
    </div>
  );
}

CMSPage.getInitialProps = async ctx => {
  console.log('ctx', ctx)
  return {  }
}

export default withRouter(CMSPage)
